mapper.select:
select member_info.*, major_info.major_id, major_info.major_name, college_info.college_id, college_info.college_name from member_info join class_info on member_info.class_id = class_info.class_id join major_info on class_info.major_id = major_info.major_id join college_info on major_info.college_id = college_info.college_id
member_info.real_name like concat(#{queryWords},'%')
and member_info.class_id = #{classId}
order by member_info.uid limit #{start}, #{limited}

select count(member_info.uid) from member_info join class_info on member_info.class_id = class_info.class_id join major_info on class_info.major_id = major_info.major_id join college_info on major_info.college_id = college_info.college_id
member_info.real_name like concat(#{queryWords},'%')
and member_info.class_id = #{classId}
