mapper.update:
update order_info
set status = ${@neko.transaction.commonbase.utils.entity.OrderStatus@CANCELED}
where order_id = #{orderId} and status = ${@neko.transaction.commonbase.utils.entity.OrderStatus@UNPAID}

mapper.select:
select order_info.*, order_detail_info.order_detail_id, order_detail_info.product_id, order_detail_info.product_name, order_detail_info.display_image, order_detail_info.full_category_name, order_detail_info.seller_uid, order_detail_info.cost product_cost, order_detail_info.actual_cost product_actual_cost, order_detail_info.number, order_detail_info.`status` product_status from order_info join order_detail_info on order_info.order_id = order_detail_info.order_id where order_info.order_id in ( select order_id from ( select distinct order_info.order_id from order_info join order_detail_info on order_info.order_id = order_detail_info.order_id
order_info.uid = #{uid}
and order_detail_info.product_name like concat(#{queryWords},'%')
and order_detail_info.status = #{status}
and order_info.status = ${@neko.transaction.commonbase.utils.entity.OrderStatus@PAID} limit #{start}, #{limited}
) sub_query ) order by order_info.order_id desc

select count(distinct order_info.order_id) from order_info
join order_detail_info
on order_info.order_id = order_detail_info.order_id

    order_info.uid = #{uid}

        and order_detail_info.product_name like concat(#{queryWords},'%')


        and order_detail_info.status = #{status}

    and order_info.status = ${@neko.transaction.commonbase.utils.entity.OrderStatus@PAID}
