mapper.select:
        select product_info.*,
               concat_ws('-', parent.category_name, category_info.category_name) full_category_name from product_info
        join category_info
        on product_info.category_id = category_info.category_id
        join category_info parent
        on category_info.parent_id = parent.category_id

            uid = #{uid}

                and product_info.product_name like concat(#{queryWords},'%')


                and product_info.status = #{status}

--             过滤已删除商品信息
            and status != ${@neko.transaction.commonbase.utils.entity.ProductStatus@DELETED}

        order by product_info.product_id desc
        limit #{start}, #{limited}

        select count(product_info.product_id) from product_info
        join category_info
        on product_info.category_id = category_info.category_id
        join category_info parent
        on category_info.parent_id = parent.category_id

            uid = #{uid}

                and product_info.product_name like concat(#{queryWords},'%')


                and product_info.status = #{status}

--             过滤已删除商品信息
            and status != ${@neko.transaction.commonbase.utils.entity.ProductStatus@DELETED}

mapper.update:
update product_info
set sale_number = sale_number + #{increase}
where product_id = #{productId}
